module("Language: gb",{setup:function(){numeral.language("gb")}});test("Format Numbers",15,function(){var a=[[1e4,"0,0.0000","10,000.0000"],[10000.23,"0,0","10,000"],[-1e4,"0,0.0","-10,000.0"],[10000.1234,"0.000","10000.123"],[-1e4,"(0,0.0000)","(10,000.0000)"],[-.23,".00","-.23"],[-.23,"(.00)","(.23)"],[.23,"0.00000","0.23000"],[1230974,"0.0a","1.2m"],[1460,"0a","1k"],[-104e3,"0a","-104k"],[1,"0o","1st"],[52,"0o","52nd"],[23,"0o","23rd"],[100,"0o","100th"]];for(var b=0;b<a.length;b++){strictEqual(numeral(a[b][0]).format(a[b][1]),a[b][2],a[b][1])}});test("Format Money",4,function(){var a=[[1000.234,"$0,0.00","£1,000.23"],[-1000.234,"($0,0)","(£1,000)"],[-1000.234,"$0.00","-£1000.23"],[1230974,"($0.00a)","£1.23m"]];for(var b=0;b<a.length;b++){strictEqual(numeral(a[b][0]).format(a[b][1]),a[b][2],a[b][1])}});test("Format Percentages",4,function(){var a=[[1,"0%","100%"],[.974878234,"0.000%","97.488%"],[-.43,"0%","-43%"],[.43,"(0.000%)","43.000%"]];for(var b=0;b<a.length;b++){strictEqual(numeral(a[b][0]).format(a[b][1]),a[b][2],a[b][1])}});test("Unformat",9,function(){var a=[["10,000.123",10000.123],["(0.12345)",-.12345],["(£1.23m)",-123e4],["10k",1e4],["-10k",-1e4],["23rd",23],["£10,000.00",1e4],["-76%",-.76],["2:23:57",8637]];for(var b=0;b<a.length;b++){strictEqual(numeral().unformat(a[b][0]),a[b][1],a[b][0])}})